// GETTING THE USER LANGUAGE
var userLanguage = window.navigator.userLanguage || window.navigator.language;

var STRING_ABOUT = "";

// CHECKING THE USER LANGUAGE
if (userLanguage.substring(0,2)=="es")
	{
	STRING_ABOUT = "Dise" + String.fromCharCode(241) + "ado por www.lrusso.com";
	}
	else
	{
	STRING_ABOUT = "Designed by www.lrusso.com";
	}

var RPGGame = {showDebug: false};

RPGGame.Preloader = function () {};

RPGGame.Preloader.prototype = {

	init: function ()
		{
		// SETTING THE MAXPOINTERS VALUE
		this.input.maxPointers = 1;

		// SETTING THE ROUNDPIXELS PROPERTY TO FALSE (IMPORTANT, DO NOT MODIFY)
		this.game.renderer.renderSession.roundPixels = true;

		// SCALING THE CANVAS SIZE FOR THE GAME
		var scaleX = window.innerWidth / 800;
		var scaleY = window.innerHeight / 432;
		var scale = Math.min(scaleX, scaleY);
		this.scale.scaleMode = Phaser.ScaleManager.USER_SCALE;
		this.scale.setUserScale(scale, scale);
		this.scale.pageAlignHorizontally = true;
		this.scale.pageAlignVertically = true;
		this.scale.refresh();

		// STARTING THE PHYSICS SYSTEM
		this.physics.startSystem(Phaser.Physics.P2JS);
		},

	preload: function ()
		{
		var imageWater = "data:image/png;base64,";
		var imageGrass = "data:image/png;base64,";
		var imageTree = "data:image/png;base64,";

		var imageWall = "data:image/png;base64,";
		var imageFloor = "data:image/png;base64,";
		var imageDoor = "data:image/png;base64,";
		var imageWood = "data:image/png;base64,";

		var imageArrow = "data:image/png;base64,";
		var imageCoin = "data:image/png;base64,";
		var imageHero = "data:image/png;base64,";

		var imageSoundGame = "data:image/png;base64,";
		var imageSaveGame = "data:image/png;base64,";
		var imageLoadGame = "data:image/png;base64,";

		game.load.tilemap("map", "RPGGame.json", null, Phaser.Tilemap.TILED_JSON);

		game.load.image("water", imageWater);
		game.load.image("grass", imageGrass);
		game.load.image("tree", imageTree);

		game.load.image("wall", imageWall);
		game.load.image("floor", imageFloor);
		game.load.image("door", imageDoor);
		game.load.image("wood", imageWood);

		game.load.image("soundgame", imageSoundGame);
		game.load.image("savegame", imageSaveGame);
		game.load.image("loadgame", imageLoadGame);

		game.load.image("arrow", imageArrow);
		game.load.spritesheet("coin", imageCoin, 32, 32);
		game.load.spritesheet("hero", imageHero, 32, 32);
		},

	create: function ()
		{
		// STARTING THE GAME
		this.state.start("RPGGame.Game");
		}
	};

RPGGame.Game = function (game)
	{
	this.splash = true;
	this.cursors = null;
	this.map = null;
	this.coins = null;
	this.layer = null;
	this.hero = null;

	// SCALING THE CANVAS SIZE FOR THE GAME
	function resizeF()
		{
		var scaleX = window.innerWidth / 800;
		var scaleY = window.innerHeight / 432;
		var scale = Math.min(scaleX, scaleY);
		game.scale.scaleMode = Phaser.ScaleManager.USER_SCALE;
		game.scale.setUserScale(scale, scale);
		game.scale.pageAlignHorizontally = true;
		game.scale.pageAlignVertically = true;
		game.scale.refresh();
		}

	window.addEventListener("resize", resizeF);
	};

RPGGame.Game.prototype = {

	init: function ()
		{
		this.cursors = null;
		this.map = null;
		this.coins = null;
		this.layer = null;
		this.hero = null;
		},

	create: function ()
		{
		this.map = game.add.tilemap("map");

		this.map.addTilesetImage("water");
		this.map.addTilesetImage("grass");
		this.map.addTilesetImage("tree");
		this.map.addTilesetImage("wall");
		this.map.addTilesetImage("floor");
		this.map.addTilesetImage("door");
		this.map.addTilesetImage("wood");

		this.map.setCollisionBetween(1, 3);

		this.layer = this.map.createLayer("Tile Layer 1");

		this.layer.resizeWorld();

		game.physics.startSystem(Phaser.Physics.ARCADE);

		//  Here we create our coins group
		this.coins = game.add.group();
		this.coins.enableBody = true;

		//  And now we convert all of the Tiled objects with an ID of 34 into sprites within the coins group
		this.map.createFromObjects("Object Layer 1", 34, "coin", 0, true, false, this.coins);

		//  Add animations to all of the coin sprites
		this.coins.callAll("animations.add", "animations", "spin", [0, 1, 2, 3, 4, 5], 10, true);
		this.coins.callAll("animations.play", "animations", "spin");

		this.hero = game.add.sprite(420, 301, "hero");
		this.hero.anchor.set(0.6);

		this.hero.animations.add("walk_left", [117, 118, 119, 120, 121, 122, 123, 124, 125]);
		this.hero.animations.add("walk_right", [143, 144, 145, 146, 147, 148, 149, 150, 151]);
		this.hero.animations.add("walk_up", [104, 105, 106, 107, 108, 109, 110, 111, 112]);
		this.hero.animations.add("walk_down", [130, 131, 132, 133, 134, 135, 136, 137, 138]);

		game.physics.arcade.enable(this.hero);

		// This adjusts the collision body size.
		this.hero.body.setSize(16, 23, 0, 3);
		this.hero.anchor.set(0.5);

		game.camera.follow(this.hero);

		this.cursors = game.input.keyboard.createCursorKeys();

		// ADDING THE SOUND GAME ICON
		var buttonSoundGameShadow = game.add.sprite(639, 29, "soundgame");
		buttonSoundGameShadow.anchor.set(0.5);
		buttonSoundGameShadow.tint = 0x000000;
		buttonSoundGameShadow.alpha = 0.7;
		buttonSoundGameShadow.fixedToCamera = true;
		var buttonSoundGame = this.add.button(612, 3, "soundgame", null, this, 2, 1, 0);
		buttonSoundGame.fixedToCamera = true;

		// ADDING THE SAVE GAME ICON
		var buttonSaveGameShadow = game.add.sprite(704, 29, "savegame");
		buttonSaveGameShadow.anchor.set(0.5);
		buttonSaveGameShadow.tint = 0x000000;
		buttonSaveGameShadow.alpha = 0.7;
		buttonSaveGameShadow.fixedToCamera = true;
		var buttonSaveGame = this.add.button(677, 3, "savegame", null, this, 2, 1, 0);
		buttonSaveGame.fixedToCamera = true;

		// ADDING THE LOAD GAME ICON
		var buttonLoadGameShadow = game.add.sprite(769, 29, "loadgame");
		buttonLoadGameShadow.anchor.set(0.5);
		buttonLoadGameShadow.tint = 0x000000;
		buttonLoadGameShadow.alpha = 0.7;
		buttonLoadGameShadow.fixedToCamera = true;
		var buttonLoadGame = this.add.button(742, 3, "loadgame", null, this, 2, 1, 0);
		buttonLoadGame.fixedToCamera = true;

		// CHECKING IF THE SPLASH MUST BE DISPLAYED
		if (this.splash==true)
			{
			// ADDING THE SPLASH
			var toastShadow = game.add.graphics();
			toastShadow.beginFill(0x000000, 0.4);
			toastShadow.fixedToCamera = true;
			var toastText = game.add.text(0, 0, STRING_ABOUT, { font: "bold 24px Arial", fill: "#fff", boundsAlignH: "center", boundsAlignV: "middle" });
			toastText.setShadow(3, 3, "rgba(0,0,0,0.5)", 2);
			toastText.setTextBounds(0, 370, 800, 55);
			toastText.fixedToCamera = true;
			toastShadow.drawRoundedRect(800 / 2 - toastText._width / 2 - 11, 373, toastText._width + 23, 46, 10);

			// SETTING THAT IN 3 SECONDS THE SPLASH MUST FADE OUT
			setTimeout(function()
				{
				game.add.tween(toastShadow).to({alpha: 0}, 500, Phaser.Easing.Linear.None, true);
				game.add.tween(toastText).to({alpha: 0}, 500, Phaser.Easing.Linear.None, true);
				}, 3000);

			// SETTING THAT THE SPLASH MUST NOT BE DISPLAYED AGAIN
			this.splash = false;
			}
		},

	update: function ()
		{
		game.physics.arcade.collide(this.hero, this.layer);
		game.physics.arcade.overlap(this.hero, this.coins, this.collectCoin, null, this);

		this.hero.body.velocity.x = 0;
		this.hero.body.velocity.y = 0;

		if (this.cursors.left.isDown)
			{
			game.physics.arcade.velocityFromAngle(180, 100, this.hero.body.velocity);
			this.hero.animations.play("walk_left", 30, true);
			}
		else if (this.cursors.right.isDown)
			{
			game.physics.arcade.velocityFromAngle(0, 100, this.hero.body.velocity);
			this.hero.animations.play("walk_right", 30, true);
			}
		else if (this.cursors.up.isDown)
			{
			game.physics.arcade.velocityFromAngle(-90, 100, this.hero.body.velocity);
			this.hero.animations.play("walk_up", 30, true);
			}
		else if (this.cursors.down.isDown)
			{
			game.physics.arcade.velocityFromAngle(90, 100, this.hero.body.velocity);
			this.hero.animations.play("walk_down", 30, true);
			}
		else
			{
			this.hero.animations.stop(null, true);
			}
		},

	render: function ()
		{
		//game.debug.body(this.hero);
		},

	collectCoin: function(player, coin)
		{
		coin.kill();
		}
	};

// CREATING THE GAME INSTANCE
var config = {width: 800, height: 432, renderer: Phaser.WEBGL, parent: "content", disableVisibilityChange: false};
var game = new Phaser.Game(config);

// CREATING THE STATES
game.state.add("RPGGame.Preloader", RPGGame.Preloader);
game.state.add("RPGGame.Game", RPGGame.Game);

// STARTING THE GAME PRELOADER
game.state.start("RPGGame.Preloader");